mapper.select:
select
post_id, post_title, post_content, post_time, post_commentnum, admin_id,
img_url
from nba.post


        and post_id = #{postId}


        and post_title = #{postTitle}


        and post_content = #{postContent}


        and post_time = #{postTime}


        and post_commentnum = #{postCommentnum}


        and admin_id = #{adminId}


        and img_url = #{imgUrl}
